package 每日一题;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

/**
 * @author W.Wen
 * @version : V1.0
 * @className: LK20211128
 * @description: 找到字符串中所有字母异位词
 * @date 2021-11-28
 */
public class LK20211128 {
    public List<Integer> findAnagrams(String s, String p) {
        ArrayList<Integer> res = new ArrayList<>();
        if(p.length()>s.length()){
            return res;
        }
        int l = p.length();
        int[] ints = new int[26];
        int[] ints1 = new int[26];
        for (int i = 0; i < l; i++) {
            ints[p.toCharArray()[i]-'a']++;
            ints1[s.toCharArray()[i] - 'a']++;
        }
        if (Arrays.equals(ints, ints1)) {
            res.add(0);
        }
        for (int i = 0; i < s.length() - l; i++) {
            ints1[s.toCharArray()[i] - 'a']--;
            ints1[s.toCharArray()[i + l] - 'a']++;
            if (Arrays.equals(ints, ints1)) {
                res.add(i + 1);
            }
        }
        return res;
    }

    /*private boolean isEqual(int[] ints, int[] ints1) {
        boolean res = true;
        for (int i = 0; i < ints.length; i++) {
            if (ints[i] != ints1[i]) {
                res = false;
                break;
            }
        }
        return res;
    }*/

    public static void main(String[] args) {
        LK20211128 lk20211128 = new LK20211128();
        System.out.println(lk20211128.findAnagrams("",
                "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz"));
    }
}
